package eu.europa.esig.dss.pades.validation;

import static org.junit.Assert.assertEquals;

import org.junit.Test;

import eu.europa.esig.dss.DSSDocument;
import eu.europa.esig.dss.DSSUtils;
import eu.europa.esig.dss.FileDocument;
import eu.europa.esig.dss.SignatureLevel;
import eu.europa.esig.dss.pdf.PdfObjFactory;
import eu.europa.esig.dss.pdf.openpdf.ITextPdfObjFactory;
import eu.europa.esig.dss.validation.CommonCertificateVerifier;
import eu.europa.esig.dss.validation.SignedDocumentValidator;
import eu.europa.esig.dss.validation.reports.Reports;
import eu.europa.esig.dss.validation.reports.SimpleReport;
import eu.europa.esig.dss.x509.CommonTrustedCertificateSource;

public class DSS1443Test {

	static {
		PdfObjFactory.setInstance(new ITextPdfObjFactory());
	}

	@Test
	public void testSigWithAttached() {
		DSSDocument dssDocument = new FileDocument("src/test/resources/validation/DSS-1443.pdf");
		SignedDocumentValidator validator = SignedDocumentValidator.fromDocument(dssDocument);
		CommonCertificateVerifier certificateVerifier = new CommonCertificateVerifier();
		CommonTrustedCertificateSource trustedCertSource = new CommonTrustedCertificateSource();
		trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(
				""));

		trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(
				""));
		certificateVerifier.setTrustedCertSource(trustedCertSource);
		validator.setCertificateVerifier(certificateVerifier);
		Reports reports = validator.validateDocument();

		SimpleReport simpleReport = reports.getSimpleReport();
		assertEquals(SignatureLevel.PAdES_BASELINE_LTA.toString(), simpleReport.getSignatureFormat(simpleReport.getFirstSignatureId()));
	}

}
